package expressionsolving;

public class expressions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5;
		int y=10;
		int a,b,c,d;
		 a=x + y * 2;
		 b= x - y + 2;
		 System.out.println(b);
		 c= (x+y) * 2;
		 d= y % x;

		 System.out.println(a);
		 
		 System.out.println(c);
		 System.out.println(d);
		 

	}

}
